import echarts from 'echarts'
export default {
	props: {

	},
	data() {
		return {
			chartLine: '',
			stepNum: [], //存放生态鸡步数
			timeData: [], //存放时间
			min: 0,
			dateName: '',
			display: false,
		}
	},
	mounted() {
		this.getData();

	},

	methods: {
		//初始化数据
		getData() {
			var time = new Date();
			this.chartLine = this.$echarts.init(document.getElementById('chartLineBox'), 'dark');
			this.dateName = '' + this.formatDate(time);
			// 指定图表的配置项和数据
			var option = {
				title: {
					text: '折线图堆叠'
				},
				tooltip: {
					trigger: 'axis'
				},
				legend: {
					data: ['邮件营销', '联盟广告', '视频广告', '直接访问', '搜索引擎']
				},
				grid: {
					left: '3%',
					right: '4%',
					bottom: '3%',
					containLabel: true
				},
				toolbox: {
					feature: {
						saveAsImage: {}
					}
				},
				xAxis: {
					type: 'category',
					boundaryGap: false,
					data: ['周一', '周二', '周三', '周四', '周五', '周六', '周日']
				},
				yAxis: {
					type: 'value'
				},
				series: [{
						name: '邮件营销',
						type: 'line',
						stack: '总量',
						data: [120, 132, 101, 134, 90, 230, 210]
					},
					{
						name: '联盟广告',
						type: 'line',
						stack: '总量',
						data: [220, 182, 191, 234, 290, 330, 310]
					},
					{
						name: '视频广告',
						type: 'line',
						stack: '总量',
						data: [150, 232, 201, 154, 190, 330, 410]
					},
					{
						name: '直接访问',
						type: 'line',
						stack: '总量',
						data: [320, 332, 301, 334, 390, 330, 320]
					},
					{
						name: '搜索引擎',
						type: 'line',
						stack: '总量',
						data: [820, 932, 901, 934, 1290, 1330, 1320]
					}
				]
			};
			// 使用刚指定的配置项和数据显示图表。
			this.chartLine.setOption(option);
		},

		//添加数据
		addData(value) {
			console.log(this.stepNum.length);
			if (this.stepNum.length < 8) {
				var time = new Date();
				this.stepNum.push(value);
				this.timeData.push(this.formatTime(time)); //向数组的末尾添加
				console.log('已添加');
				this.getData();
			}
			if (this.stepNum.length >= 8) {
				var time = new Date();
				console.log(value);
				this.stepNum.splice(0, 1);
				this.timeData.splice(0, 1);
				this.stepNum.push(value);
				console.log(this.formatTime(time));
				this.timeData.push(this.formatTime(time)); //向数组的末尾添加
				console.log('已添加');
				this.getData();
			}
			this.min = this.stepNum[0];

		},

		// 日期数字补0操作
		addZero(num) {
			return num < 10 ? '0' + num : num;
		},

		//2020-05-06T11:40:17.000+0000，转换
		formatDate(date) {
			var time = new Date(Date.parse(date));
			var Y = time.getFullYear() + '-';
			var M = this.addZero(time.getMonth() + 1) + '-';
			var D = this.addZero(time.getDate());
			return Y + M + D;
		},
		//转换日期格式 正常,这里只需要时间
		formatTime(date) {
			var time = new Date(Date.parse(date));
			var h = this.addZero(time.getHours()) + ':';
			var m = this.addZero(time.getMinutes()) + ':';
			var s = this.addZero(time.getSeconds());
			return h + m + s;
			// }
		},
	},
	watch: {
		'stepInfor.livestockId': {
			handler: function() {
				console.log('watch  Step');
				console.log(this.stepNum);
				console.log('watch  timeData');
				console.log(this.timeData);
				this.stepNum = [];
				this.timeData = [];
			},
			deep: true, //深度监听
		}
	},
	beforeDestroy() {
		//组件销毁执行
		//清除定时器
		clearInterval(this.timer);
		console.log("beforeDestroy");
	},
	destroyed() {
		// 销毁监听事件
		//清除定时器
		//clearInterval(this.timer);
		console.log("destroyed");
	}
}
